-- Version PostgreSQL du schéma DnD Shop Generator

-- Table des presets de boutiques
CREATE TABLE IF NOT EXISTS shoppreset (
    id SERIAL PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    description TEXT,
    wealthlevel VARCHAR(50) NOT NULL,
    shoptype VARCHAR(100) NOT NULL,
    typechances TEXT NOT NULL,
    rarityconfig TEXT NOT NULL,
    isdefault BOOLEAN NOT NULL DEFAULT false,
    createdat TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,
    updatedat TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP
);

-- Table des objets
CREATE TABLE IF NOT EXISTS items (
    index SERIAL PRIMARY KEY,
    nomobjet VARCHAR(255),
    type VARCHAR(100),
    soustype VARCHAR(100),
    maitrise VARCHAR(100),
    rarete VARCHAR(50),
    caracteristiques TEXT,
    valeur VARCHAR(50),
    infosupplementaire TEXT,
    poids VARCHAR(50),
    source VARCHAR(100)
);

-- Table des boutiques
CREATE TABLE IF NOT EXISTS shop (
    id SERIAL PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    description TEXT,
    items TEXT NOT NULL,
    createdat TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP
);

